/* eslint-disable react/prop-types */
import { useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getProductDetails } from "../reducers/productsSlice";
import { Button, Card, Col, Image, ListGroup, Row, Form } from "react-bootstrap";
import Rating from "../components/Rating";
import Loader from "../components/Loader";
import Message from "../components/Message";
 
function ProductScreen() {
  const { product, isLoading, isError, error } = useSelector(
    (state) => state.products
  );

  const [ qty, setQty] = useS
  const dispatch = useDispatch();
  const { id } = useParams();
 
  useEffect(() => {
    dispatch(getProductDetails(id));
  }, [dispatch, id]);
  return (
    <div>
      {isLoading && <Loader />}
      {isError && <Message varient="danger">{error}</Message>}
 
      <Link to="/" className="btn btn-light my-3">
        Go Back
      </Link>
      <Row>
        <Col md={6}>
          <Image style={{height: '500px', width: '500px'}} src={product.image} alt={product.name} />
        </Col>
 
        <Col md={3}>
          <ListGroup variant="flush">
            <ListGroup.Item>
              <h3>{product.name}</h3>
            </ListGroup.Item>
 
            <ListGroup.Item>
              <Rating
                value={product.rating}
                text={`${product.numReviews} reviews`}
                color={"#f8e825"}
              />
            </ListGroup.Item>
 
            <ListGroup.Item>Price: ${product.price}</ListGroup.Item>
 
            <ListGroup.Item>Descriprtion: {product.description}</ListGroup.Item>
          </ListGroup>
        </Col>
 
        <Col md={3}>
          <Card>
            <ListGroup variant="flush">
              <ListGroup.Item>
                <Row>
                  <Col>Price:</Col>
                  <Col>
                    <strong>${product.price}</strong>
                  </Col>
                </Row>
              </ListGroup.Item>
 
              <ListGroup.Item>
                <Row>
                  <Col>Status:</Col>
                  <Col>
                    {product.countInStock > 0 ? "In Stock" : "Out of Stock"}
                  </Col>
                </Row>
              </ListGroup.Item>
 
              <ListGroup.Item>
                <Button
                  className="btn-block"
                  type="button"
                  disabled={product.countInStock === 0}
                >
                  Add to cart
                </Button>
              </ListGroup.Item>
            </ListGroup>
          </Card>
        </Col>
      </Row>
    </div>
  );
}
 
export default ProductScreen;
