import React, { useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { listProductDetails } from '../actions/productActions'; // Adjust import path as needed

function ProductScreen() {
  const { id } = useParams();
  const dispatch = useDispatch();
  
  // Select product details from the Redux store
  const productDetails = useSelector((state) => state.productDetails);
  const { product, loading, error } = productDetails;

  // Fetch product details when the component mounts or `id` changes
  useEffect(() => {
    dispatch(listProductDetails(id));
  }, [dispatch, id]); // Add dependencies here

  // Handle loading and error states
  if (loading) return <h2>Loading...</h2>;
  if (error) return <h2>Error: {error}</h2>;

  // Check if product data exists
  if (!product) return <h2>Product not found</h2>;

  return (
    <div>
      <h1>{product.name}</h1>
      {/* Render other product details */}
    </div>
  );
}

export default ProductScreen;
